package week1.day1;

public class Learndatatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// browser
		//datatype variable name = value
		// start letter is lower case
		
		int nooftabs = 4;
		char logo = 'C';  //single variable
		float version = 121.09f; // last one char should be user
		String browsername= "firefox";  // it is no pre defined,it is user defined
		boolean browserisOpened = true;
		
		System.out.println("Number of tabs :" +nooftabs);
		System.out.println("Browser logo :" +logo);
		System.out.println("Browser version :" +version);
		System.out.println("Browser name :" +browsername +"\n"+"BrowserOpened :" +browserisOpened); // For coming line  in next line using"\n"
		

	}

}